var $body = (window.opera) ? (document.compatMode == "CSS1Compat" ? $('html') : $('body')) : $('html,body');

function scrollToId(id,headerH){
				var h = (headerH)?headerH:0;
				$body.stop().animate({
					scrollTop: $(id).offset().top - h
				}, 1000);
}

function popup_flag(){
	$('#mask').toggleClass('on');
}

$(function(){
	tab()
});

function tab(){
	// $('.tab_container').css('width',$('.tab_btns li').length+'00%')
	// $('.tab_container li').css('width',100/$('.tab_container li').length+'%')
	$('.tab_btns li').click(function(){
		$(this).siblings().removeClass('on');
		$(this).addClass('on');
		$('.tab_container').css('left','-'+$(this).index()+'00%')
	})
	// $('.tab_container')
}

function toggle_flag(_this){
	if($(_this).parent().hasClass('on')){
		$(_this).parent().removeClass('on');
		$(_this).next('.toggle_container').slideUp();
	}else{
		if($(_this).parent().siblings().hasClass('on')){
			$('.toggle_container').slideUp();
			$('.toggle_box li').removeClass('on');
		}
			
		$(_this).parent().addClass('on');
		$(_this).next('.toggle_container').slideDown();
	}
	
}

